import { Component} from '@angular/core';
import {
  FormBuilder,
  FormGroup,
  FormArray,
  FormControl,
  ValidatorFn
} from '@angular/forms';
import { of } from 'rxjs';
import { ApiserviceService } from 'src/app/apiservice.service';

@Component({
  selector: 'app-tienda-articulos',
  templateUrl: './tienda-articulos.component.html',
  styleUrls: ['./tienda-articulos.component.css']
})
export class TiendaArticulosComponent  {

  formc: FormGroup;
  ordersData = [
    { id: 100, name: 'order 1' },
    { id: 200, name: 'order 2' },
    { id: 300, name: 'order 3' },
    { id: 400, name: 'order 4' }
  ];
  tiendas = [{id:'',sucursal: ''} ];

  get ordersFormArray() {
    return this.formc.controls.orders as FormArray;
  }

  constructor(private formBuilder: FormBuilder, private service: ApiserviceService) {
    this.formc = this.formBuilder.group({
      orders: new FormArray([]),
      tiendas: ['']
    });

    this.addCheckboxes();

        // async orders
        of(this.getOrders()).subscribe(tiendas => {
          this.tiendas = tiendas;
          this.formc.controls.tiendas.patchValue(this.tiendas[0].id);
        });


  }

  private addCheckboxes() {
    this.ordersData.forEach(() => this.ordersFormArray.push(new FormControl(false)));
  }

  refreshDepList() {
    this.service.getArticulosList().subscribe(data => {
      console.log(data);
      this.tiendas = data;
      this.DepartmentListWithoutFilter = data;
    });
  }



  getOrders() {
    return [
      { id: '1', name: 'order 1' },
      { id: '2', name: 'order 2' },
      { id: '3', name: 'order 3' },
      { id: '4', name: 'order 4' }
    ];
  }

  submit() {
    const selectedOrderIds = this.formc.value.orders
      .map((checked:  boolean, i: number) => checked ? this.ordersData[i].id : null)
      .filter((v: null) => v !== null);
    console.log(selectedOrderIds);
  }
}
